import React, { useState } from 'react';
import Modal from 'react-modal';
import MypageOption from './MypageOption';
import { BottomWrapper, PostPreview } from './MypagePostStyle';
import preview from '../../assets/img/profile.jpg';
import Post from '../post/Post';

const MypagePost: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const onClick = (id: number) => {
    setIsOpen(true);
    console.log(id);
  };
  const dummyList = [
    { id: 0, img: preview },
    { id: 1, img: preview },
  ];
  return (
    <>
      <MypageOption />
      <BottomWrapper>
        {dummyList.map((content) => (
          <div key={content.id}>
            <Modal
              isOpen={isOpen}
              onRequestClose={() => setIsOpen(false)}
              style={{
                overlay: {
                  position: 'fixed',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  backgroundColor: 'rgba(0, 0, 0, 0.25)',
                },
                content: {
                  position: 'absolute',
                  top: '100px',
                  left: '100px',
                  right: '100px',
                  bottom: '100px',
                  background: '#fff',
                  overflow: 'auto',
                  WebkitOverflowScrolling: 'touch',
                  borderRadius: '4px',
                  outline: 'none',
                  padding: '20px',
                  display: 'flex',
                  justifyContent: 'center',
                },
              }}
            >
              <Post />
              <div>{content.id}</div>
            </Modal>
            <PostPreview src={content.img} onClick={onClick(content.id)} />
          </div>
        ))}
        {/* <PostPreview src={preveiw} />
        <PostPreview src={preveiw} />
        <PostPreview src={preveiw} />
        <PostPreview src={preveiw} /> */}
      </BottomWrapper>
    </>
  );
};

export default MypagePost;
