import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const HeaderContainer = styled.div`
  display: flex;
  justify-content: center;
`;

export const HeaderWrapper = styled.div`
  width: 1194px;
  height: 60px;
  border-radius: 36px;
  margin-top: 30px;
  display: flex;
  align-items: center;
  box-shadow: 6px 6px 12px rgba(0, 0, 0, 0.16), -10px -10px 20px #ffffff;
  justify-content: space-between;
`;

export const HeaderTitle = styled.div`
  font-size: 28px;
  color: #919191;
  font-family: 'SCDreamBold';
  margin-left: 50px;
  :hover {
    color: #1d1d1d;
    transition: 1s;
  }
`;

export const MypageLink = styled(Link)`
  font-size: 18px;
  color: #919191;
  font-family: 'SCDreamMedium';
  margin-right: 50px;
  :hover {
    color: #1d1d1d;
    transition: 1s;
  }
  text-decoration: none;
`;

export const Search = styled.input`
  font-size: 18px;
  color: #919191;
  font-family: 'SCDreamMedium';
  width: 400px;
  margin-left: 50px;
  margin-right: 50px;
  border: none;
  :focus::placeholder {
    color: transparent;
  }
`;
