import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import {
  BottomWrapper,
  ButtonsWrapper,
  PostButton,
  ReviewButton,
} from './MypageOptionStyle';

const MypageOption: React.FC = () => {
  const location = useLocation();
  const current = location.pathname;
  const [isPost, setIsPost] = useState(false);
  const [isReview, setIsReview] = useState(false);
  useEffect(() => {
    switch (current) {
      case '/mypage/review':
        setIsReview(true);
        break;
      default:
        setIsPost(true);
    }
  }, []);
  return (
    <BottomWrapper>
      <ButtonsWrapper>
        <PostButton>hi</PostButton>
        <ReviewButton>hi</ReviewButton>
      </ButtonsWrapper>
    </BottomWrapper>
  );
};

export default MypageOption;
